nta.declaration:
const int NUM_PIECES = 5; 
const int NUM_OUT_SENSORS = 2;
const int NUM_SLOTS = 30;

/** Change this to modify number of stations and input sensors **/
            const int NUM_STATIONS = 3;
            const int NUM_IN_SENSORS = 3;
/****************************************************************/

typedef int[0, NUM_STATIONS-1] id_s;
typedef int[0, NUM_IN_SENSORS-1] id_sens;

bool belt[NUM_SLOTS];
int stations[NUM_SLOTS];
int in_sensors[NUM_SLOTS];
int out_sensors[NUM_SLOTS];
int preprocessing[NUM_SLOTS];

bool status_in_sensors[NUM_IN_SENSORS];
bool status_stations[NUM_STATIONS];
bool status_preprocessing[NUM_STATIONS];

chan enter_preprocessing[NUM_STATIONS], free_in_sensor[NUM_IN_SENSORS], sensor_busy[NUM_IN_SENSORS];
broadcast chan in_sensor, step, initialize;

int max_len_queue[NUM_STATIONS];
int len_queue[NUM_STATIONS];

int belt_count;

void update_belt_count() {
    int i;
    belt_count = 0;
    for (i = 0; i < NUM_SLOTS; i++) {
        if (belt[i]) {
        belt_count++;
        }
    }
}

void initializer() {

int i;

    out_sensors[1] = 1;
    in_sensors[5] = 1;
    stations[7] = 1;
    preprocessing[6] = 1;
    max_len_queue[0] = 5;


    out_sensors[10] = 2;
    in_sensors[16] = 2;
    stations[20] = 2;
    preprocessing[17] = 2;
    preprocessing[18] = 2;
    preprocessing[19] = 2;
    max_len_queue[1] = 7;

    in_sensors[23] = 3;
    stations[25] = 3; //da provare con stazione 24
    preprocessing[24] = 3;   
    max_len_queue[2] = 3;

    for ( i = 0; i < NUM_PIECES; i++ ){
        belt[i] = 1;
    }

}

template.declaration:
clock y;
clock x;
int processing_counter = 0;
int in_sens_pos;
int out_sens_pos;
int prev_stat_pos;
int next_id;
int prev_id;
int len_queue_verifier;

int get_len_pre_processing() {
    int i, station, sensor;

    for (i = 0; i < NUM_SLOTS; i++) {
        if (stations[i] == (id + 1)) station = i;
        if (in_sensors[i] == (id + 1)) sensor = i;
    }
    if (sensor > station) return NUM_SLOTS - sensor + station;
    else return station-sensor;
}

int get_processing_time() { //fai un array di processing times
    return in_sens_pos*5;
}

void enqueue() {
    len_queue[next_id]++;
}

void dequeue() {
    status_preprocessing[id] = 1;
    if (!belt[in_sens_pos]) status_in_sensors[id] = 0;
    if (len_queue[id]>0) len_queue[id]--;
}

void free_preprocessing(){
    //status_stations[id] = 1;
    status_preprocessing[id] = 0;
}

void free_all() {
    status_stations[id] = 0;
    status_preprocessing[id] = 0;
    if (!belt[in_sens_pos]) status_in_sensors[id] = 0;
}

void free_station(){
    status_stations[id] = 0;
}

void set_parameters(){
    int i;
    out_sens_pos = -1;

    if (id == NUM_STATIONS - 1) next_id = 0;
    else next_id = id + 1;

    if (id == 0) prev_id = NUM_STATIONS - 1;
    else prev_id = id - 1;

    for ( i = 0; i < NUM_SLOTS; i++ ){
        if (in_sensors[i] == id+1) in_sens_pos = i;
        if (out_sensors[i] == id+1) out_sens_pos = i;
        if (stations[i] == prev_id+1) prev_stat_pos = i;
    }

}

void set_len_queue() {
    int i;
    len_queue_verifier = 0;
    for (i = prev_stat_pos+1; i <= in_sens_pos; i++){
        if (belt[i]) len_queue_verifier++;
    }
}

clock x;

void shift() {
    int next, prec, i, tmp, tmplast;

    bool last = belt[NUM_SLOTS-1];
    for (i = NUM_SLOTS-1; i >= 0; i--){
        if (i == 0) {
            if (!last) {
                tmplast = belt[NUM_SLOTS-1];
                belt[NUM_SLOTS-1] = last;
            } 
            next = 0;
            prec = NUM_SLOTS-1; 
        } else {
            next = i;
            prec = i-1;
        }
        if (belt[prec] && !belt[next]){

            if (stations[next] > 0){
                tmp = belt[next];
                belt[next] = belt[prec];
                belt[prec] = tmp;
                status_stations[stations[next]-1] = 1;
            } else if (in_sensors[next] > 0){
                tmp = belt[next];
                belt[next] = belt[prec];
                belt[prec] = tmp;
                status_in_sensors[in_sensors[next]-1] = 1;   
            } else if(in_sensors[prec] > 0 && !status_preprocessing[in_sensors[prec]-1] && !status_stations[in_sensors[prec]-1]){
                tmp = belt[next];
                belt[next] = belt[prec];
                belt[prec] = tmp;             
            } else if (stations[prec] && !status_stations[stations[prec]-1]) { 
               tmp = belt[next];
               belt[next] = belt[prec];
               belt[prec] = tmp;
            } else if (preprocessing[next] > 0 && preprocessing[prec] > 0) {
                    tmp = belt[next];
                    belt[next] = belt[prec];
                    belt[prec] = tmp;
            } else if (!belt[next] && (stations[next] == 0) && (in_sensors[next] == 0) && (preprocessing[next] == 0) && (stations[prec] == 0) && (in_sensors[prec] == 0) && (preprocessing[prec] == 0)){
                    tmp = belt[next];
                    belt[next] = belt[prec];
                    belt[prec] = tmp;
            }
        }
    }    
    if (!last) belt[NUM_SLOTS-1] = tmplast;
}

int check_insensor(){
    int i;
    for(i=0; i< NUM_IN_SENSORS; i++){
        if(status_in_sensors[i] == 1) return 1;
    }
    return 0;
}
