nta.declaration:
// Place global declarations here.
const int NUM_PIECES = 3; 
const int NUM_STATIONS = 4;
const int NUM_IN_SENSORS = 4;
const int NUM_OUT_SENSORS = 4;
const int NUM_SLOTS = 95;

typedef int[0, NUM_STATIONS-1] id_s;

bool belt[NUM_SLOTS];
int stations[NUM_SLOTS];
int in_sensors[NUM_SLOTS];
int out_sensors[NUM_SLOTS];
int preprocessing[NUM_SLOTS];

bool status_in_sensors[NUM_IN_SENSORS];
bool status_stations[NUM_STATIONS];
bool status_preprocessing[NUM_STATIONS];

chan enter_preprocessing[NUM_STATIONS], free_in_sensor[NUM_IN_SENSORS];
broadcast chan initializer, in_sensor;

int max_len_queue[NUM_STATIONS];
int len_queue[NUM_STATIONS];

template.declaration:
void initialize() {

    int i;

    out_sensors[81] = 1;
    in_sensors[94] = 1;
    stations[2] = 1;
    max_len_queue[0] = 13;
    out_sensors[9] = 2;
    in_sensors[19] = 2;
    stations[23] = 2;
    max_len_queue[1] = 10;
    out_sensors[53] = 3;
    in_sensors[62] = 3;
    stations[66] = 3;
    max_len_queue[2] = 9;
    out_sensors[74] = 4;
    in_sensors[76] = 4;
    stations[78] = 4;
    max_len_queue[3] = 2;

    for(i = 23; i < 23 + NUM_PIECES; i++){
        belt[i] = 1;
    }

    for (i=0; i< NUM_STATIONS;i++){
        status_stations[i] = 0;
        status_in_sensors[i] = 0;
        status_preprocessing[i] = 0;
        len_queue[i] = 0;
    }



}

clock y;

int get_len_pre_processing() {
    int i, station, sensor;

    for (i = 0; i < NUM_SLOTS; i++) {
        if (stations[i] == id) station = i;
        if (in_sensors[i] == id) sensor = i;
    }
    if (sensor > station) return NUM_SLOTS - sensor + station;
    else return station-sensor;
}

int get_processing_time() {
    return 5;
}

void enqueue() {
    len_queue[next_id]++;
}

void dequeue() {
    status_preprocessing[id] = 1;
    if (len_queue[id]>0) len_queue[id]--;
}

void update_station_status(){
    status_stations[id] = 1;
}

void update_status() {
    status_stations[id] = 0;
    status_preprocessing[id] = 0;
    status_in_sensors[id] = 0;
}

clock x;

void shift() {
    int next, i;

    bool temp = belt[NUM_SLOTS-1];
    for (i = NUM_SLOTS-1; i > 0; i--){
        if (belt[i-1] && !belt[i]){
            next = i;
            if (stations[next] > 0){
                if (!status_stations[stations[next]-1]) {
                    belt[i] = belt[i-1];
                    belt[i-1] = false;
                }
            } else if (in_sensors[next] > 0){
                if (!status_in_sensors[in_sensors[next]-1] && !status_preprocessing[in_sensors[next]-1]) { //ho aggiunto se Ã¨ anche nello stato di preprocessing controlare domani le condizioni
                    belt[i] = belt[i-1];
                    belt[i-1] = false;
                    status_in_sensors[in_sensors[next]-1] = 1;                    
                }    
            } else if (stations[i-1] && !status_stations[stations[i-1]-1]) {
                belt[i] = belt[i-1];
                belt[i-1] = false;
            } else if (preprocessing[next] > 0 && !status_preprocessing[preprocessing[next]-1]) {
                belt[i] = belt[i-1];
                belt[i-1] = false;
            } else if (!belt[next] && (stations[next] == 0) && (in_sensors[next] == 0) && (preprocessing[next] == 0)){
                belt[i] = belt[i-1];
                belt[i-1] = false;
            } 
        }
    }    
    belt[0] = temp;
}

bool is_first = true;

void update_sensor_status(){
    status_in_sensors[id] = 0;
}

nta.system:
initialization = Initialization();
processing_station1 = Processing_Station(0, 1);
