processing_station4 = Processing_Station(3, 0);
sensor1 = Sensor(0);
sensor2 = Sensor(1);
sensor3 = Sensor(2);
sensor4 = Sensor(3);
conveyor_belt = Conveyor_Belt();

system initialization, processing_station1, processing_station2, processing_station3, processing_station4, conveyor_belt, sensor1, sensor2, sensor3, sensor4;
