nta.declaration:
const int NUM_PIECES = 3; 
const int NUM_SLOTS = 88;
const int GATE_POS = 11;
const int FIRST_POS_BELT2 = 29;
const int LAST_POS_BELT2 = 42;
const int RULE_CONTROLLER = 10;

/** Change this to modify number of stations and input sensors **/
            const int NUM_STATIONS = 6;
            const int NUM_IN_SENSORS = 6;
            const int NUM_OUT_SENSORS = 5;
/****************************************************************/

// time required for an entire loop: 746,92
typedef int[0, NUM_STATIONS-1] id_s;
typedef int[0, NUM_IN_SENSORS-1] id_sens;
typedef int[0, NUM_IN_SENSORS-1] id_outsens;

bool belt[NUM_SLOTS];
int stations[NUM_SLOTS];
int in_sensors[NUM_SLOTS];
int out_sensors[NUM_SLOTS];
int preprocessing[NUM_SLOTS];

bool status_in_sensors[NUM_IN_SENSORS];
bool status_stations[NUM_STATIONS];
bool status_preprocessing[NUM_STATIONS];

broadcast chan enter_preprocessing[NUM_STATIONS], free_in_sensor[NUM_IN_SENSORS], check_outsensor[NUM_IN_SENSORS], busy[NUM_IN_SENSORS], free[NUM_IN_SENSORS];
broadcast chan initialize, step;

int previous_station[NUM_STATIONS];
int max_len_queue[NUM_STATIONS];
int len_queue[NUM_STATIONS];

int belt_count;

void update_belt_count() {
    int i;
    belt_count = 0;
    for (i = 0; i < NUM_SLOTS; i++) {
        if (belt[i]) {
        belt_count++;
        }
    }
}

void initializer() {

    int i;

    out_sensors[57] = 1;
    in_sensors[63] = 1;
    stations[68] = 1;
    previous_station[0] = 53;
    max_len_queue[0] = abs(63 - 53 + 1); // input sensor - previous station + 1

    out_sensors[71] = 2;
    in_sensors[74] = 2;
    stations[78] = 2;
    previous_station[1] = 68;
    max_len_queue[1] = abs(74 - 68 + 1);

    stations[32] = 3;
    in_sensors[30] = 3;
    previous_station[2] = 78;
    max_len_queue[2] = abs(30 - 78 + 1);

    out_sensors[34] = 4;
    in_sensors[37] = 4;
    stations[39] = 4;
    previous_station[3] = 32;
    max_len_queue[3] = abs(37 - 32 + 1);

    out_sensors[19] = 5;
    in_sensors[22] = 5;
    stations[25] = 5;
    previous_station[4] = 78;
    max_len_queue[4] = abs(22 - 78 + 1);

    out_sensors[48] = 6;
    in_sensors[52] = 6;
    stations[54] = 6;
    previous_station[5] = 25;
    max_len_queue[5] = abs(52 - 25 + 1);    

    for ( i = 58; i < NUM_PIECES + 58; i++ ){
        belt[i] = 1;
    }    

}

template.declaration:
clock y;
int processing_counter = 0;
int in_sens_pos;
int out_sens_pos;
int tmp_next_id;
int next_id;
int next_st;
int next_id2;
int processing_time;
int pos_curr_station;

int get_len_pre_processing() {
    int i, station, sensor;

    for (i = 0; i < NUM_SLOTS; i++) {
        if (stations[i] == (id + 1)) station = i;
        if (in_sensors[i] == (id + 1)) sensor = i;
    }
    if (sensor > station) return NUM_SLOTS - sensor + station;
    else return station-sensor;
}

void update_preprocessing(){
    int i;
    for (i = 0; i < NUM_SLOTS; i++) {
        if ((preprocessing[i] == (id + 1)||stations[i] == (id + 1)) && belt[i] == 1)   status_preprocessing[id] = 1; // probabilmente controlli inutili
    }

    if (!belt[in_sens_pos]) status_in_sensors[id] = 0;
}

void free_preprocessing(){
    //status_stations[id] = 1;

    status_preprocessing[id] = 0;
    processing_time = in_sens_pos;
}

void free_all(){
    status_stations[id] = 0;
    status_preprocessing[id] = 0;
    //if (!belt[in_sens_pos]) status_in_sensors[id] = 0;
}

void free_station(){
    status_stations[id] = 0;
}

void set_parameters(){ //da sistemare per seconda branch
    int i, j, k;
    next_id = -1;
    next_id2 = -1;

    for(j = 0; j < NUM_SLOTS; j++){
        if(in_sensors[j] == id + 1){
            in_sens_pos = j;
            k = j+1;
            while(stations[k] != id + 1){
                preprocessing[k] = id + 1;
                k++;
            }
            pos_curr_station = k;
        }
    }

    for(i = 0; i < NUM_SLOTS; i++){
            if(out_sensors[i] == id + 1){
                max_len_queue[id] = 0;
                k = i; 
                while(in_sensors[k] != id + 1){
                    max_len_queue[id]++;
                    k++;
                }
                max_len_queue[id]++;
            }
    }

    for(i = pos_curr_station; i < NUM_SLOTS; i++){

        if(i == NUM_SLOTS - 1) i = 0;

        if(out_sensors[i] > 0){
            next_id = out_sensors[i] - 1;
            i = NUM_SLOTS;
        }else if(in_sensors[i] > 0){
            i = NUM_SLOTS;
        }else if(i == GATE_POS && i < FIRST_POS_BELT2 && i > LAST_POS_BELT2){
            for(k = FIRST_POS_BELT2; k < LAST_POS_BELT2; k++){
                if(out_sensors[i] > 0){
                    next_id2 = out_sensors[i] - 1;
                    k = LAST_POS_BELT2;
                }else if(in_sensors[i] > 0){
                    k = LAST_POS_BELT2;
                }
            }
            for(k = GATE_POS + 1; k < NUM_SLOTS; k++){
                if(k == FIRST_POS_BELT2 - 1) k = LAST_POS_BELT2 + 1;
                if(out_sensors[i] > 0){
                    next_id = out_sensors[i] - 1;
                    k = NUM_SLOTS;
                }else if(in_sensors[i] > 0){
                    k = NUM_SLOTS;
                }
            }
            i = NUM_SLOTS;
        }
    } 
}

