int out_sensor_pos;

void set_pos(){    
    int i;

    for ( i = 0; i < NUM_SLOTS; i++ ){
        if (out_sensors[i] == id+1) out_sensor_pos = i;
    }

}

clock x;
bool gate;
int counter_gate;

int get_index(){
    int i;

    for (i = 0; i < NUM_SLOTS-1; i++){
        if (!belt[i] && !belt[i+1]){
            return i;
        }
    }

    return NUM_SLOTS;
}

// return position of in sensor
int get_in_sens_position(int id){
    int i;

    for (i = 0; i < NUM_SLOTS; i++) {
        if (in_sensors[i] == id+1) return i;
    }
    return -2;
}

// return position of out sensor if present, otherwise return -1
int get_out_sens_position(int id){
    int i;

    for (i = 0; i < NUM_SLOTS; i++) {
        if (out_sensors[i] == id+1) return i;
    }
    return -1;
}

void set_len_queue(){
    int i, j;
    int out_sens_pos;
    int in_sens_pos;
    int count;

    for (j = 0; j < NUM_STATIONS; j++){
        out_sens_pos = get_out_sens_position(j);
        in_sens_pos = get_in_sens_position(j);
        count = 0;

        if (out_sens_pos != -1){ // out_sensor present
            for (i = out_sens_pos; i <= in_sens_pos; i++){
                if (belt[i]) count ++;
            }
        } else { // out_sensor not present, counting from previous station
            for (i = previous_station[j]; i <= in_sens_pos; i++){
                if (belt[i]) count ++;
            }
        }
        len_queue[j] = count;
    }  

}

void shift() {
    int next, prec, i, tmp, tmpfirst, first_index;

    bool first = belt[0];
    bool second = belt[1];
    bool last = belt[NUM_SLOTS-1];
    bool semilast = belt[NUM_SLOTS-2];

    first_index = get_index();
    i = first_index;

    while (i != first_index+1) {

        prec = i;        

        if (i == NUM_SLOTS-1) next = 0;
        else next = i+1;

        if(i == GATE_POS){
            if(counter_gate%RULE_CONTROLLER){
                gate = true;
            }else{
                gate = false;
                counter_gate = 0;
            }
             if(!gate){
                 next = FIRST_POS_BELT2;
                 prec = GATE_POS;
             }else{
                 next = i+1;
                 prec = i;  
             }
        } else if(i == LAST_POS_BELT2){ // exit from secondary belt
            next = FIRST_POS_BELT2 - 1;
            prec = LAST_POS_BELT2;
        }else if(i == FIRST_POS_BELT2 - 1){ // interleaving of the two belts
            next = LAST_POS_BELT2 + 1;
            prec = FIRST_POS_BELT2 - 1;
        }

        if (belt[prec] && !belt[next]){

            if (stations[next] > 0){
                tmp = belt[next];
                belt[next] = belt[prec];
                belt[prec] = tmp;
                // status_stations[stations[next]-1] = 1;
            } else if (in_sensors[next] > 0 && !status_in_sensors[in_sensors[next]-1]){
                tmp = belt[next];
                belt[next] = belt[prec];
                belt[prec] = tmp;
                // status_in_sensors[in_sensors[next]-1] = 1;
            } else if(in_sensors[prec] > 0 && !status_preprocessing[in_sensors[prec]-1] && !status_stations[in_sensors[prec]-1]){
                tmp = belt[next];
                belt[next] = belt[prec];
                belt[prec] = tmp;     
                // status_preprocessing[in_sensors[prec]-1] = 1;      
            } else if (stations[prec] && !status_stations[stations[prec]-1]) { 
               tmp = belt[next];
               belt[next] = belt[prec];
               belt[prec] = tmp;
            } else if (preprocessing[next] > 0 && preprocessing[prec] > 0) {
                    tmp = belt[next];
                    belt[next] = belt[prec];
                    belt[prec] = tmp;
            } else if (!belt[next] && (stations[next] == 0) && (in_sensors[next] == 0) && (preprocessing[next] == 0) && (stations[prec] == 0) && (in_sensors[prec] == 0) && (preprocessing[prec] == 0)){
                    tmp = belt[next];
                    belt[next] = belt[prec];
                    belt[prec] = tmp;
            }
        }

        if (i != 0) i--;
        else i = NUM_SLOTS-1;
    }
}

int in_sensor_pos;
clock x;

void set_pos(){    
    int i;

    for ( i = 0; i < NUM_SLOTS; i++ ){
        if (in_sensors[i] == id+1) in_sensor_pos = i;
    }

}

void update_sensor_status(){
    status_in_sensors[id] = 1;
}

