nta.declaration:
const int NUM_PIECES = 5; 
const int NUM_OUT_SENSORS = 5;
const int NUM_SLOTS = 88;
const int GATE_POS = 11;

/** Change this to modify number of stations and input sensors **/
            const int NUM_STATIONS = 6;
            const int NUM_IN_SENSORS = 6;
/****************************************************************/

typedef int[0, NUM_STATIONS-1] id_s;
typedef int[0, NUM_IN_SENSORS-1] id_sens;

bool belt[NUM_SLOTS];
int stations[NUM_SLOTS];
int in_sensors[NUM_SLOTS];
int out_sensors[NUM_SLOTS];
int preprocessing[NUM_SLOTS];

bool status_in_sensors[NUM_IN_SENSORS];
bool status_stations[NUM_STATIONS];
bool status_preprocessing[NUM_STATIONS];

const double processing_mean = 30.0; 
const double processing_variance = 5.0;

chan enter_preprocessing[NUM_STATIONS], free_in_sensor[NUM_IN_SENSORS], sensor_busy[NUM_IN_SENSORS];
broadcast chan in_sensor, step, initialize;

int max_len_queue[NUM_STATIONS];
int len_queue[NUM_STATIONS];

int belt_count;

void update_belt_count() {
    int i;
    belt_count = 0;
    for (i = 0; i < NUM_SLOTS; i++) {
        if (belt[i]) {
        belt_count++;
        }
    }
}

void initializer() {

int i;

    out_sensors[57] = 1;
    in_sensors[63] = 1;
    stations[68] = 1;
    preprocessing[64] = 1;

    out_sensors[72] = 2;
    in_sensors[74] = 2;
    stations[78] = 2;
    preprocessing[75] = 2;

    in_sensors[30] = 3;
    stations[32] = 3;
    preprocessing[31] = 3;

    out_sensors[35] = 4;
    in_sensors[37] = 4;
    stations[39] = 4;
    preprocessing[38] = 4;

    out_sensors[17] = 5;
    in_sensors[22] = 5;
    stations[25] = 5;
    preprocessing[23] = 5;

    out_sensors[48] = 6;
    in_sensors[52] = 6;
    stations[53] = 6;
    preprocessing[53] = 6;


    for ( i = NUM_SLOTS-6; i < NUM_PIECES + NUM_SLOTS-6; i++ ){
        belt[i] = 1;
    }

}

template.declaration:
clock x;
int processing_counter = 0;
int in_sens_pos;
int out_sens_pos;
int next_id[2];
int len_queue_verifier;
int processing_time;

int get_len_pre_processing() {
    int i, station, sensor;

    for (i = 0; i < NUM_SLOTS; i++) {
        if (stations[i] == (id + 1)) station = i;
        if (in_sensors[i] == (id + 1)) sensor = i;
    }
    if (sensor > station) return NUM_SLOTS - sensor + station;
    else return station-sensor;
}

void enqueue(){
    len_queue[next_id]++;
}

void dequeue(){
    status_preprocessing[id] = 1;
    if (!belt[in_sens_pos]) status_in_sensors[id] = 0;
    if (len_queue[id]>0) len_queue[id]--;
}

void free_preprocessing(){
    //status_stations[id] = 1;
    status_preprocessing[id] = 0;
    processing_time = fint(random_normal(processing_mean, sqrt(processing_variance)));
}

void free_all(){
    status_stations[id] = 0;
    status_preprocessing[id] = 0;
    if (!belt[in_sens_pos]) status_in_sensors[id] = 0;
}

void free_station(){
    status_stations[id] = 0;
}

void set_parameters(){
    int i;
    next_id[0] = -1;
    next_id[1] = -1;
    out_sens_pos = -1;

    if (id == NUM_STATIONS - 1) next_id[0] = 0;
    else if (id == 1){
        next_id[0] = 2;
        next_id[1] = 4;
    }else if(id == 3) next_id[0] = 5;
    else next_id[0] = id + 1;

    for ( i = 0; i < NUM_SLOTS; i++ ){
        if (in_sensors[i] == id+1) in_sens_pos = i;
        if (out_sensors[i] == id+1) out_sens_pos = i;
    }
}

void set_len_queue() {
    int i;
    len_queue_verifier = 0;
    for (i = prev_stat_pos+1; i <= in_sens_pos; i++){
        if (belt[i]) len_queue_verifier++;
    }
}

clock x;
bool gate;
int counter_gate;

int get_index(){
    int i;

    for (i = 0; i < NUM_SLOTS-1; i++){
        if (!belt[i] && !belt[i+1]){
            return i;
        }
    }

    return NUM_SLOTS;
}

void shift() {
    int next, prec, i, tmp, tmpfirst, first_index;

    bool first = belt[0];
    bool second = belt[1];
    bool last = belt[NUM_SLOTS-1];
    bool semilast = belt[NUM_SLOTS-2];

    first_index = get_index();
    i = first_index;

    while (i != first_index+1) {

        prec = i;        

        if (i == NUM_SLOTS-1) next = 0;
        else next = i+1;

        if(i == 5){ // gate in position 5
            if(counter_gate%10){
                gate = true;
            }else{
                gate = false;
                counter_gate = 0;
            }
             if(!gate){
                 next = 14;
                 prec = 5;
             }else{
                 next = i+1;
                 prec = i;  
             }
        } else if(i == 19){ // exit from secondary belt
            next = 13;
            prec = 19;
        }else if(i == 13){ // interleaving of the two belts
            next = 20;
            prec = 13;
        }

        if (belt[prec] && !belt[next]){

            if (stations[next] > 0){
                tmp = belt[next];
                belt[next] = belt[prec];
                belt[prec] = tmp;
                status_stations[stations[next]-1] = 1;
            } else if (in_sensors[next] > 0){
                tmp = belt[next];
                belt[next] = belt[prec];
                belt[prec] = tmp;
                status_in_sensors[in_sensors[next]-1] = 1;   
            } else if(in_sensors[prec] > 0 && !status_preprocessing[in_sensors[prec]-1] && !status_stations[in_sensors[prec]-1]){
                tmp = belt[next];
                belt[next] = belt[prec];
                belt[prec] = tmp;             
            } else if (stations[prec] && !status_stations[stations[prec]-1]) { 
               tmp = belt[next];
               belt[next] = belt[prec];
               belt[prec] = tmp;
            } else if (preprocessing[next] > 0 && preprocessing[prec] > 0) {
                    tmp = belt[next];
                    belt[next] = belt[prec];
                    belt[prec] = tmp;
            } else if (!belt[next] && (stations[next] == 0) && (in_sensors[next] == 0) && (preprocessing[next] == 0) && (stations[prec] == 0) && (in_sensors[prec] == 0) && (preprocessing[prec] == 0)){
                    tmp = belt[next];
                    belt[next] = belt[prec];
                    belt[prec] = tmp;
            }
        }

        if (i != 0) i--;
        else i = NUM_SLOTS-1;
    }



