int processing_time;

int get_len_pre_processing() {
    int i, station, sensor;

    for (i = 0; i < NUM_SLOTS; i++) {
        if (stations[i] == (id + 1)) station = i;
        if (in_sensors[i] == (id + 1)) sensor = i;
    }
    if (sensor > station) return NUM_SLOTS - sensor + station;
    else return station-sensor;
}

void update_preprocessing(){
    status_preprocessing[id] = 1;
    if (!belt[in_sens_pos]) status_in_sensors[id] = 0;
}

void free_preprocessing(){
    //status_stations[id] = 1;
    status_preprocessing[id] = 0;
    processing_time = fint(random_normal(processing_mean, sqrt(processing_variance)));
}

void free_all(){
    status_stations[id] = 0;
    status_preprocessing[id] = 0;
    if (!belt[in_sens_pos]) status_in_sensors[id] = 0;
}

void free_station(){
    status_stations[id] = 0;
}

void set_parameters(){ //da sistemare per seconda branch
    int i;
    next_id = -1;
    next_id2 = -1;

    if (id == NUM_STATIONS - 1) next_st = 0;
    else next_st = id + 1;


    for ( i = 0; i < NUM_SLOTS; i++ ){
        if (out_sensors[i] == next_st+1) next_id = next_st;
    }

    //controlla se giusto assegnamento di next_id 

    //per nextid2 invece bisogna capire 
}

